// user.service.ts
import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';

interface User {
  id: number;
  name: string;
  email: string;
  role: string;
  isActive: boolean;
}

@Injectable({
  providedIn: 'root' // Makes service available throughout the app
})
export class UserService {
  // Private data that only the service can modify
  private users: User[] = [
    { id: 1, name: 'John Doe', email: 'john@example.com', role: 'admin', isActive: true },
    { id: 2, name: 'Jane Smith', email: 'jane@example.com', role: 'user', isActive: true },
    { id: 3, name: 'Mike Johnson', email: 'mike@example.com', role: 'user', isActive: false }
  ];

  private currentUserSubject = new BehaviorSubject<User | null>(null);
  public currentUser$ = this.currentUserSubject.asObservable();

  // Constructor can inject other services
  constructor() {
    console.log('UserService initialized');
  }

  // Get all users
  getAllUsers(): User[] {
    return [...this.users]; // Return a copy to prevent external modification
  }

  // Get user by ID
  getUserById(id: number): User | undefined {
    return this.users.find(user => user.id === id);
  }

  // Add new user
  addUser(user: Omit<User, 'id'>): User {
    const newUser: User = {
      ...user,
      id: this.getNextId()
    };
    this.users.push(newUser);
    return newUser;
  }

  // Update existing user
  updateUser(id: number, updates: Partial<User>): User | null {
    const userIndex = this.users.findIndex(user => user.id === id);
    if (userIndex !== -1) {
      this.users[userIndex] = { ...this.users[userIndex], ...updates };
      return this.users[userIndex];
    }
    return null;
  }

  // Delete user
  deleteUser(id: number): boolean {
    const userIndex = this.users.findIndex(user => user.id === id);
    if (userIndex !== -1) {
      this.users.splice(userIndex, 1);
      return true;
    }
    return false;
  }

  // Set current user (for login simulation)
  setCurrentUser(user: User): void {
    this.currentUserSubject.next(user);
  }

  // Clear current user (for logout)
  clearCurrentUser(): void {
    this.currentUserSubject.next(null);
  }

  // Get current user synchronously
  getCurrentUser(): User | null {
    return this.currentUserSubject.value;
  }

  // Utility methods
  private getNextId(): number {
    return Math.max(...this.users.map(u => u.id), 0) + 1;
  }

  // Filter users by role
  getUsersByRole(role: string): User[] {
    return this.users.filter(user => user.role === role);
  }

  // Get active users count
  getActiveUsersCount(): number {
    return this.users.filter(user => user.isActive).length;
  }

  // Search users by name or email
  searchUsers(query: string): User[] {
    const lowercaseQuery = query.toLowerCase();
    return this.users.filter(user => 
      user.name.toLowerCase().includes(lowercaseQuery) ||
      user.email.toLowerCase().includes(lowercaseQuery)
    );
  }
}

// Using the service in a component
// user-list.component.ts
import { Component, OnInit, OnDestroy } from '@angular/core';
import { UserService } from './user.service';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-user-list',
  template: `
    <div class="user-list">
      <h3>Users ({{ users.length }})</h3>

      <div *ngIf="currentUser" class="current-user">
        <p>Logged in as: {{ currentUser.name }} ({{ currentUser.role }})</p>
        <button (click)="logout()">Logout</button>
      </div>

      <div class="user-stats">
        <p>Active Users: {{ activeUsersCount }}</p>
        <p>Admin Users: {{ adminUsers.length }}</p>
      </div>

      <input 
        type="text" 
        [(ngModel)]="searchQuery"
        (input)="onSearch()"
        placeholder="Search users...">

      <div *ngFor="let user of filteredUsers" class="user-item">
        <h4>{{ user.name }}</h4>
        <p>{{ user.email }} - {{ user.role }}</p>
        <p>Status: {{ user.isActive ? 'Active' : 'Inactive' }}</p>
        <button (click)="loginAs(user)">Login as {{ user.name }}</button>
        <button (click)="toggleUserStatus(user.id)">Toggle Status</button>
        <button (click)="deleteUser(user.id)">Delete</button>
      </div>
    </div>
  `
})
export class UserListComponent implements OnInit, OnDestroy {
  users: User[] = [];
  filteredUsers: User[] = [];
  currentUser: User | null = null;
  adminUsers: User[] = [];
  activeUsersCount = 0;
  searchQuery = '';

  private subscription = new Subscription();

  constructor(private userService: UserService) {}

  ngOnInit() {
    // Load initial data
    this.loadUsers();

    // Subscribe to current user changes
    this.subscription.add(
      this.userService.currentUser$.subscribe(user => {
        this.currentUser = user;
      })
    );
  }

  ngOnDestroy() {
    this.subscription.unsubscribe();
  }

  loadUsers() {
    this.users = this.userService.getAllUsers();
    this.filteredUsers = [...this.users];
    this.adminUsers = this.userService.getUsersByRole('admin');
    this.activeUsersCount = this.userService.getActiveUsersCount();
  }

  onSearch() {
    if (this.searchQuery.trim()) {
      this.filteredUsers = this.userService.searchUsers(this.searchQuery);
    } else {
      this.filteredUsers = [...this.users];
    }
  }

  loginAs(user: User) {
    this.userService.setCurrentUser(user);
  }

  logout() {
    this.userService.clearCurrentUser();
  }

  toggleUserStatus(userId: number) {
    const user = this.userService.getUserById(userId);
    if (user) {
      this.userService.updateUser(userId, { isActive: !user.isActive });
      this.loadUsers(); // Refresh data
      this.onSearch(); // Refresh filtered data
    }
  }

  deleteUser(userId: number) {
    if (confirm('Are you sure you want to delete this user?')) {
      this.userService.deleteUser(userId);
      this.loadUsers(); // Refresh data
      this.onSearch(); // Refresh filtered data
    }
  }
}

// api.service.ts
import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders, HttpErrorResponse } from '@angular/common/http';
import { Observable, throwError, BehaviorSubject, of } from 'rxjs';
import { catchError, map, tap, delay, retry } from 'rxjs/operators';

interface Post {
  id: number;
  title: string;
  body: string;
  userId: number;
}

interface Comment {
  id: number;
  postId: number;
  name: string;
  email: string;
  body: string;
}

interface ApiResponse<T> {
  data: T;
  success: boolean;
  message: string;
}

@Injectable({
  providedIn: 'root'
})
export class ApiService {
  private readonly baseUrl = 'https://jsonplaceholder.typicode.com';
  private loadingSubject = new BehaviorSubject<boolean>(false);
  public loading$ = this.loadingSubject.asObservable();

  private httpOptions = {
    headers: new HttpHeaders({
      'Content-Type': 'application/json',
      'Accept': 'application/json'
    })
  };

  constructor(private http: HttpClient) {
    console.log('ApiService initialized');
  }

  // Generic GET request with error handling
  private get<T>(endpoint: string): Observable<T> {
    this.setLoading(true);

    return this.http.get<T>(`${this.baseUrl}${endpoint}`, this.httpOptions)
      .pipe(
        retry(2), // Retry failed requests twice
        tap(() => this.setLoading(false)),
        catchError(this.handleError)
      );
  }

  // Generic POST request
  private post<T>(endpoint: string, data: any): Observable<T> {
    this.setLoading(true);

    return this.http.post<T>(`${this.baseUrl}${endpoint}`, data, this.httpOptions)
      .pipe(
        tap(() => this.setLoading(false)),
        catchError(this.handleError)
      );
  }

  // Generic PUT request
  private put<T>(endpoint: string, data: any): Observable<T> {
    this.setLoading(true);

    return this.http.put<T>(`${this.baseUrl}${endpoint}`, data, this.httpOptions)
      .pipe(
        tap(() => this.setLoading(false)),
        catchError(this.handleError)
      );
  }

  // Generic DELETE request
  private delete<T>(endpoint: string): Observable<T> {
    this.setLoading(true);

    return this.http.delete<T>(`${this.baseUrl}${endpoint}`, this.httpOptions)
      .pipe(
        tap(() => this.setLoading(false)),
        catchError(this.handleError)
      );
  }

  // Posts API methods
  getAllPosts(): Observable<Post[]> {
    return this.get<Post[]>('/posts')
      .pipe(
        map(posts => posts.slice(0, 10)), // Limit to first 10 posts
        tap(posts => console.log(`Loaded ${posts.length} posts`))
      );
  }

  getPostById(id: number): Observable<Post> {
    return this.get<Post>(`/posts/${id}`);
  }

  getPostsByUserId(userId: number): Observable<Post[]> {
    return this.get<Post[]>(`/posts?userId=${userId}`);
  }

  createPost(post: Omit<Post, 'id'>): Observable<Post> {
    return this.post<Post>('/posts', post)
      .pipe(
        tap(newPost => console.log('Created post:', newPost))
      );
  }

  updatePost(id: number, post: Partial<Post>): Observable<Post> {
    return this.put<Post>(`/posts/${id}`, post)
      .pipe(
        tap(updatedPost => console.log('Updated post:', updatedPost))
      );
  }

  deletePost(id: number): Observable<any> {
    return this.delete<any>(`/posts/${id}`)
      .pipe(
        tap(() => console.log(`Deleted post ${id}`))
      );
  }

  // Comments API methods
  getCommentsByPostId(postId: number): Observable<Comment[]> {
    return this.get<Comment[]>(`/posts/${postId}/comments`);
  }

  // Search posts (simulated with filtering)
  searchPosts(query: string): Observable<Post[]> {
    return this.getAllPosts()
      .pipe(
        map(posts => posts.filter(post => 
          post.title.toLowerCase().includes(query.toLowerCase()) ||
          post.body.toLowerCase().includes(query.toLowerCase())
        )),
        delay(500) // Simulate network delay
      );
  }

  // Utility methods
  private setLoading(loading: boolean): void {
    this.loadingSubject.next(loading);
  }

  private handleError = (error: HttpErrorResponse): Observable<never> => {
    this.setLoading(false);

    let errorMessage = 'An unknown error occurred';

    if (error.error instanceof ErrorEvent) {
      // Client-side error
      errorMessage = `Client Error: ${error.error.message}`;
    } else {
      // Server-side error
      errorMessage = `Server Error Code: ${error.status}\nMessage: ${error.message}`;
    }

    console.error('API Error:', errorMessage);
    return throwError(() => new Error(errorMessage));
  }

  // Cache management (simple example)
  private cache = new Map<string, any>();

  getCachedPosts(): Observable<Post[]> {
    const cacheKey = 'all-posts';
    const cached = this.cache.get(cacheKey);

    if (cached) {
      console.log('Returning cached posts');
      return of(cached);
    }

    return this.getAllPosts()
      .pipe(
        tap(posts => {
          this.cache.set(cacheKey, posts);
          console.log('Posts cached');
        })
      );
  }

  clearCache(): void {
    this.cache.clear();
    console.log('Cache cleared');
  }
}

// Using the HTTP service in a component
// posts.component.ts
import { Component, OnInit, OnDestroy } from '@angular/core';
import { ApiService } from './api.service';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-posts',
  template: `
    <div class="posts-container">
      <h2>Posts Management</h2>

      <!-- Loading indicator -->
      <div *ngIf="isLoading" class="loading">
        <p>üîÑ Loading posts...</p>
      </div>

      <!-- Error message -->
      <div *ngIf="errorMessage" class="error">
        <p>‚ùå {{ errorMessage }}</p>
        <button (click)="loadPosts()">Retry</button>
      </div>

      <!-- Search -->
      <div class="search-section">
        <input 
          type="text" 
          [(ngModel)]="searchQuery"
          (keyup.enter)="searchPosts()"
          placeholder="Search posts...">
        <button (click)="searchPosts()" [disabled]="isLoading">Search</button>
        <button (click)="loadPosts()" [disabled]="isLoading">Show All</button>
        <button (click)="loadCachedPosts()" [disabled]="isLoading">Load Cached</button>
      </div>

      <!-- Posts list -->
      <div class="posts-list">
        <div *ngFor="let post of posts" class="post-item">
          <h3>{{ post.title }}</h3>
          <p>{{ post.body }}</p>
          <div class="post-actions">
            <button (click)="loadComments(post.id)" [disabled]="isLoading">
              Load Comments
            </button>
            <button (click)="editPost(post)" [disabled]="isLoading">
              Edit
            </button>
            <button (click)="deletePost(post.id)" [disabled]="isLoading">
              Delete
            </button>
          </div>

          <!-- Comments -->
          <div *ngIf="postComments[post.id]" class="comments">
            <h4>Comments:</h4>
            <div *ngFor="let comment of postComments[post.id]" class="comment">
              <strong>{{ comment.name }}</strong> ({{ comment.email }})
              <p>{{ comment.body }}</p>
            </div>
          </div>
        </div>
      </div>

      <!-- Create new post -->
      <div class="create-post">
        <h3>Create New Post</h3>
        <input [(ngModel)]="newPost.title" placeholder="Post title">
        <textarea [(ngModel)]="newPost.body" placeholder="Post content"></textarea>
        <button (click)="createPost()" [disabled]="isLoading || !newPost.title || !newPost.body">
          Create Post
        </button>
      </div>
    </div>
  `
})
export class PostsComponent implements OnInit, OnDestroy {
  posts: Post[] = [];
  postComments: { [postId: number]: Comment[] } = {};
  isLoading = false;
  errorMessage = '';
  searchQuery = '';

  newPost = {
    title: '',
    body: '',
    userId: 1
  };

  private subscriptions = new Subscription();

  constructor(private apiService: ApiService) {}

  ngOnInit() {
    this.loadPosts();

    // Subscribe to loading state
    this.subscriptions.add(
      this.apiService.loading$.subscribe(loading => {
        this.isLoading = loading;
      })
    );
  }

  ngOnDestroy() {
    this.subscriptions.unsubscribe();
  }

  loadPosts() {
    this.errorMessage = '';

    this.subscriptions.add(
      this.apiService.getAllPosts().subscribe({
        next: (posts) => {
          this.posts = posts;
          console.log('Posts loaded successfully');
        },
        error: (error) => {
          this.errorMessage = error.message;
          console.error('Failed to load posts:', error);
        }
      })
    );
  }

  loadCachedPosts() {
    this.subscriptions.add(
      this.apiService.getCachedPosts().subscribe({
        next: (posts) => {
          this.posts = posts;
        },
        error: (error) => {
          this.errorMessage = error.message;
        }
      })
    );
  }

  searchPosts() {
    if (!this.searchQuery.trim()) {
      this.loadPosts();
      return;
    }

    this.subscriptions.add(
      this.apiService.searchPosts(this.searchQuery).subscribe({
        next: (posts) => {
          this.posts = posts;
        },
        error: (error) => {
          this.errorMessage = error.message;
        }
      })
    );
  }

  loadComments(postId: number) {
    this.subscriptions.add(
      this.apiService.getCommentsByPostId(postId).subscribe({
        next: (comments) => {
          this.postComments[postId] = comments;
        },
        error: (error) => {
          console.error('Failed to load comments:', error);
        }
      })
    );
  }

  createPost() {
    this.subscriptions.add(
      this.apiService.createPost(this.newPost).subscribe({
        next: (post) => {
          this.posts.unshift(post); // Add to beginning of list
          this.newPost = { title: '', body: '', userId: 1 }; // Reset form
          console.log('Post created successfully');
        },
        error: (error) => {
          this.errorMessage = error.message;
        }
      })
    );
  }

  editPost(post: Post) {
    const newTitle = prompt('Enter new title:', post.title);
    if (newTitle && newTitle !== post.title) {
      this.subscriptions.add(
        this.apiService.updatePost(post.id, { title: newTitle }).subscribe({
          next: (updatedPost) => {
            const index = this.posts.findIndex(p => p.id === post.id);
            if (index !== -1) {
              this.posts[index] = updatedPost;
            }
          },
          error: (error) => {
            this.errorMessage = error.message;
          }
        })
      );
    }
  }

  deletePost(postId: number) {
    if (confirm('Are you sure you want to delete this post?')) {
      this.subscriptions.add(
        this.apiService.deletePost(postId).subscribe({
          next: () => {
            this.posts = this.posts.filter(p => p.id !== postId);
            delete this.postComments[postId];
          },
          error: (error) => {
            this.errorMessage = error.message;
          }
        })
      );
    }
  }
}

// shopping-cart.service.ts
import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable, combineLatest } from 'rxjs';
import { map, tap } from 'rxjs/operators';

interface CartItem {
  id: number;
  name: string;
  price: number;
  quantity: number;
  image?: string;
  category: string;
}

interface CartState {
  items: CartItem[];
  totalItems: number;
  totalPrice: number;
  isLoading: boolean;
  lastUpdated: Date;
}

interface Product {
  id: number;
  name: string;
  price: number;
  image?: string;
  category: string;
  inStock: boolean;
}

@Injectable({
  providedIn: 'root'
})
export class ShoppingCartService {
  // Private state subjects
  private cartItemsSubject = new BehaviorSubject<CartItem[]>([]);
  private loadingSubject = new BehaviorSubject<boolean>(false);
  private lastUpdatedSubject = new BehaviorSubject<Date>(new Date());

  // Public observables
  public cartItems$ = this.cartItemsSubject.asObservable();
  public isLoading$ = this.loadingSubject.asObservable();
  public lastUpdated$ = this.lastUpdatedSubject.asObservable();

  // Computed observables
  public totalItems$ = this.cartItems$.pipe(
    map(items => items.reduce((total, item) => total + item.quantity, 0))
  );

  public totalPrice$ = this.cartItems$.pipe(
    map(items => items.reduce((total, item) => total + (item.price * item.quantity), 0))
  );

  public cartSummary$ = combineLatest([
    this.cartItems$,
    this.totalItems$,
    this.totalPrice$,
    this.isLoading$,
    this.lastUpdated$
  ]).pipe(
    map(([items, totalItems, totalPrice, isLoading, lastUpdated]) => ({
      items,
      totalItems,
      totalPrice,
      isLoading,
      lastUpdated
    } as CartState))
  );

  // Cart item count by category
  public itemsByCategory$ = this.cartItems$.pipe(
    map(items => {
      const categories: { [key: string]: number } = {};
      items.forEach(item => {
        categories[item.category] = (categories[item.category] || 0) + item.quantity;
      });
      return categories;
    })
  );

  // Check if cart is empty
  public isEmpty$ = this.cartItems$.pipe(
    map(items => items.length === 0)
  );

  // Check if cart has items
  public hasItems$ = this.cartItems$.pipe(
    map(items => items.length > 0)
  );

  constructor() {
    // Load cart from localStorage on service initialization
    this.loadCartFromStorage();

    // Auto-save cart to localStorage whenever items change
    this.cartItems$.subscribe(items => {
      this.saveCartToStorage(items);
    });

    console.log('ShoppingCartService initialized');
  }

  // Add item to cart
  addItem(product: Product, quantity: number = 1): void {
    if (!product.inStock) {
      console.warn('Cannot add out-of-stock item to cart');
      return;
    }

    this.setLoading(true);

    // Simulate async operation
    setTimeout(() => {
      const currentItems = this.cartItemsSubject.value;
      const existingItemIndex = currentItems.findIndex(item => item.id === product.id);

      let updatedItems: CartItem[];

      if (existingItemIndex >= 0) {
        // Update existing item quantity
        updatedItems = currentItems.map((item, index) => 
          index === existingItemIndex 
            ? { ...item, quantity: item.quantity + quantity }
            : item
        );
      } else {
        // Add new item
        const newItem: CartItem = {
          id: product.id,
          name: product.name,
          price: product.price,
          quantity,
          image: product.image,
          category: product.category
        };
        updatedItems = [...currentItems, newItem];
      }

      this.cartItemsSubject.next(updatedItems);
      this.updateTimestamp();
      this.setLoading(false);

      console.log(`Added ${quantity} x ${product.name} to cart`);
    }, 300);
  }

  // Remove item from cart
  removeItem(productId: number): void {
    this.setLoading(true);

    setTimeout(() => {
      const currentItems = this.cartItemsSubject.value;
      const updatedItems = currentItems.filter(item => item.id !== productId);

      this.cartItemsSubject.next(updatedItems);
      this.updateTimestamp();
      this.setLoading(false);

      console.log(`Removed item ${productId} from cart`);
    }, 200);
  }

  // Update item quantity
  updateQuantity(productId: number, quantity: number): void {
    if (quantity <= 0) {
      this.removeItem(productId);
      return;
    }

    this.setLoading(true);

    setTimeout(() => {
      const currentItems = this.cartItemsSubject.value;
      const updatedItems = currentItems.map(item => 
        item.id === productId 
          ? { ...item, quantity }
          : item
      );

      this.cartItemsSubject.next(updatedItems);
      this.updateTimestamp();
      this.setLoading(false);

      console.log(`Updated quantity for item ${productId} to ${quantity}`);
    }, 200);
  }

  // Clear entire cart
  clearCart(): void {
    this.setLoading(true);

    setTimeout(() => {
      this.cartItemsSubject.next([]);
      this.updateTimestamp();
      this.setLoading(false);

      console.log('Cart cleared');
    }, 300);
  }

  // Get current cart items synchronously
  getCurrentItems(): CartItem[] {
    return this.cartItemsSubject.value;
  }

  // Get item by ID
  getItem(productId: number): CartItem | undefined {
    return this.cartItemsSubject.value.find(item => item.id === productId);
  }

  // Check if item is in cart
  hasItem(productId: number): boolean {
    return this.cartItemsSubject.value.some(item => item.id === productId);
  }

  // Get item quantity
  getItemQuantity(productId: number): number {
    const item = this.getItem(productId);
    return item ? item.quantity : 0;
  }

  // Calculate discount (example business logic)
  calculateDiscount(totalPrice: number): number {
    if (totalPrice >= 100) return 0.1; // 10% discount for orders over $100
    if (totalPrice >= 50) return 0.05;  // 5% discount for orders over $50
    return 0;
  }

  // Get discounted total
  public discountedTotal$ = this.totalPrice$.pipe(
    map(total => {
      const discount = this.calculateDiscount(total);
      return {
        originalTotal: total,
        discount: discount,
        discountAmount: total * discount,
        finalTotal: total * (1 - discount)
      };
    })
  );

  // Apply coupon code (simulated)
  applyCoupon(code: string): Observable<boolean> {
    return new Observable(observer => {
      this.setLoading(true);

      // Simulate API call to validate coupon
      setTimeout(() => {
        const validCoupons = ['SAVE10', 'DISCOUNT20', 'FREESHIP'];
        const isValid = validCoupons.includes(code.toUpperCase());

        if (isValid) {
          console.log(`Applied coupon: ${code}`);
          // In a real app, you'd update cart with coupon discount
        }

        this.setLoading(false);
        observer.next(isValid);
        observer.complete();
      }, 1000);
    });
  }

  // Checkout simulation
  checkout(): Observable<{ success: boolean; orderId?: string; error?: string }> {
    return new Observable(observer => {
      this.setLoading(true);

      // Simulate checkout process
      setTimeout(() => {
        const items = this.cartItemsSubject.value;

        if (items.length === 0) {
          observer.next({ success: false, error: 'Cart is empty' });
        } else {
          // Simulate successful checkout
          const orderId = 'ORD-' + Date.now();
          this.clearCart();
          observer.next({ success: true, orderId });
        }

        this.setLoading(false);
        observer.complete();
      }, 2000);
    });
  }

  // Private utility methods
  private setLoading(loading: boolean): void {
    this.loadingSubject.next(loading);
  }

  private updateTimestamp(): void {
    this.lastUpdatedSubject.next(new Date());
  }

  private saveCartToStorage(items: CartItem[]): void {
    try {
      localStorage.setItem('shopping-cart', JSON.stringify(items));
    } catch (error) {
      console.error('Failed to save cart to localStorage:', error);
    }
  }

  private loadCartFromStorage(): void {
    try {
      const saved = localStorage.getItem('shopping-cart');
      if (saved) {
        const items = JSON.parse(saved) as CartItem[];
        this.cartItemsSubject.next(items);
        console.log('Cart loaded from localStorage');
      }
    } catch (error) {
      console.error('Failed to load cart from localStorage:', error);
    }
  }
}

// Using the state management service
// cart.component.ts
import { Component, OnInit, OnDestroy } from '@angular/core';
import { ShoppingCartService } from './shopping-cart.service';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-cart',
  template: `
    <div class="cart-container">
      <h2>Shopping Cart</h2>

      <!-- Loading indicator -->
      <div *ngIf="(cartService.isLoading$ | async)" class="loading">
        Loading...
      </div>

      <!-- Empty cart message -->
      <div *ngIf="(cartService.isEmpty$ | async) && !(cartService.isLoading$ | async)" class="empty-cart">
        <p>üõí Your cart is empty</p>
        <button (click)="addSampleItems()">Add Sample Items</button>
      </div>

      <!-- Cart items -->
      <div *ngIf="(cartService.hasItems$ | async)" class="cart-content">
        <!-- Cart summary -->
        <div class="cart-summary">
          <p><strong>Total Items:</strong> {{ cartService.totalItems$ | async }}</p>
          <p><strong>Total Price:</strong> ${{ (cartService.totalPrice$ | async) | number:'1.2-2' }}</p>
          <p><strong>Last Updated:</strong> {{ (cartService.lastUpdated$ | async) | date:'short' }}</p>
        </div>

        <!-- Discount info -->
        <div class="discount-info" *ngIf="(cartService.discountedTotal$ | async) as discount">
          <p *ngIf="discount.discount > 0" class="discount-applied">
            üéâ {{ (discount.discount * 100) }}% discount applied! 
            Save ${{ discount.discountAmount | number:'1.2-2' }}
          </p>
          <p><strong>Final Total:</strong> ${{ discount.finalTotal | number:'1.2-2' }}</p>
        </div>

        <!-- Items by category -->
        <div class="category-summary">
          <h4>Items by Category:</h4>
          <div *ngFor="let category of (cartService.itemsByCategory$ | async) | keyvalue">
            {{ category.key }}: {{ category.value }} items
          </div>
        </div>

        <!-- Cart items list -->
        <div class="cart-items">
          <div *ngFor="let item of (cartService.cartItems$ | async)" class="cart-item">
            <h4>{{ item.name }}</h4>
            <p>Price: ${{ item.price | number:'1.2-2' }}</p>
            <p>Category: {{ item.category }}</p>

            <div class="quantity-controls">
              <button (click)="decreaseQuantity(item.id)" [disabled]="(cartService.isLoading$ | async)">-</button>
              <span class="quantity">{{ item.quantity }}</span>
              <button (click)="increaseQuantity(item.id)" [disabled]="(cartService.isLoading$ | async)">+</button>
            </div>

            <p><strong>Subtotal:</strong> ${{ (item.price * item.quantity) | number:'1.2-2' }}</p>

            <button (click)="removeItem(item.id)" [disabled]="(cartService.isLoading$ | async)" class="remove-btn">
              Remove
            </button>
          </div>
        </div>

        <!-- Cart actions -->
        <div class="cart-actions">
          <button (click)="clearCart()" [disabled]="(cartService.isLoading$ | async)" class="clear-btn">
            Clear Cart
          </button>

          <button (click)="checkout()" [disabled]="(cartService.isLoading$ | async)" class="checkout-btn">
            Checkout
          </button>
        </div>

        <!-- Coupon section -->
        <div class="coupon-section">
          <input [(ngModel)]="couponCode" placeholder="Enter coupon code">
          <button (click)="applyCoupon()" [disabled]="(cartService.isLoading$ | async) || !couponCode">
            Apply Coupon
          </button>
        </div>
      </div>

      <!-- Checkout result -->
      <div *ngIf="checkoutResult" class="checkout-result">
        <div *ngIf="checkoutResult.success" class="success">
          ‚úÖ Order placed successfully! Order ID: {{ checkoutResult.orderId }}
        </div>
        <div *ngIf="!checkoutResult.success" class="error">
          ‚ùå Checkout failed: {{ checkoutResult.error }}
        </div>
      </div>
    </div>
  `
})
export class CartComponent implements OnInit, OnDestroy {
  couponCode = '';
  checkoutResult: any = null;
  private subscriptions = new Subscription();

  constructor(public cartService: ShoppingCartService) {}

  ngOnInit() {
    // Subscribe to cart changes for logging
    this.subscriptions.add(
      this.cartService.cartSummary$.subscribe(summary => {
        console.log('Cart Summary:', summary);
      })
    );
  }

  ngOnDestroy() {
    this.subscriptions.unsubscribe();
  }

  addSampleItems() {
    const sampleProducts: Product[] = [
      { id: 1, name: 'Laptop', price: 999, category: 'Electronics', inStock: true },
      { id: 2, name: 'Book', price: 25, category: 'Education', inStock: true },
      { id: 3, name: 'Coffee Mug', price: 15, category: 'Kitchen', inStock: true }
    ];

    sampleProducts.forEach(product => {
      this.cartService.addItem(product, 1);
    });
  }

  increaseQuantity(productId: number) {
    const currentQuantity = this.cartService.getItemQuantity(productId);
    this.cartService.updateQuantity(productId, currentQuantity + 1);
  }

  decreaseQuantity(productId: number) {
    const currentQuantity = this.cartService.getItemQuantity(productId);
    if (currentQuantity > 1) {
      this.cartService.updateQuantity(productId, currentQuantity - 1);
    } else {
      this.cartService.removeItem(productId);
    }
  }

  removeItem(productId: number) {
    this.cartService.removeItem(productId);
  }

  clearCart() {
    if (confirm('Are you sure you want to clear the cart?')) {
      this.cartService.clearCart();
    }
  }

  applyCoupon() {
    this.subscriptions.add(
      this.cartService.applyCoupon(this.couponCode).subscribe(isValid => {
        if (isValid) {
          alert('Coupon applied successfully!');
          this.couponCode = '';
        } else {
          alert('Invalid coupon code');
        }
      })
    );
  }

  checkout() {
    this.subscriptions.add(
      this.cartService.checkout().subscribe(result => {
        this.checkoutResult = result;

        // Clear result after 5 seconds
        setTimeout(() => {
          this.checkoutResult = null;
        }, 5000);
      })
    );
  }
}
