import { Component } from '@angular/core';

@Component({
  selector: 'app-hello-world',
  template: `
    <h1>Hello, {{name}}!</h1>
    <p>Welcome to Angular development.</p>
  `,
  styles: [`
    h1 { color: #1976d2; }
    p { font-style: italic; }
  `]
})
export class HelloWorldComponent {
  name = 'Angular Developer';
}

exercises.solution:
import { Component } from '@angular/core';

@Component({
  selector: 'app-personal-greeting',
  template: `
    <div class="greeting">
      <h2>Hi, I'm {{name}}!</h2>
      <p>My favorite language is {{favoriteLanguage}}</p>
    </div>
  `,
  styles: [`
    .greeting {
      padding: 20px;
      border: 2px solid #1976d2;
      border-radius: 8px;
      text-align: center;
    }
  `]
})
export class PersonalGreetingComponent {
  name = 'Your Name';
  favoriteLanguage = 'TypeScript';
}
